import { Octokit } from '@octokit/rest';
import { execSync } from 'child_process';
import fs from 'fs';
import path from 'path';

let connectionSettings;

async function getAccessToken() {
  if (connectionSettings && connectionSettings.settings.expires_at && new Date(connectionSettings.settings.expires_at).getTime() > Date.now()) {
    return connectionSettings.settings.access_token;
  }

  const hostname = process.env.REPLIT_CONNECTORS_HOSTNAME;
  const xReplitToken = process.env.REPL_IDENTITY 
    ? 'repl ' + process.env.REPL_IDENTITY 
    : process.env.WEB_REPL_RENEWAL 
    ? 'depl ' + process.env.WEB_REPL_RENEWAL 
    : null;

  if (!xReplitToken) {
    throw new Error('X_REPLIT_TOKEN not found for repl/depl');
  }

  connectionSettings = await fetch(
    'https://' + hostname + '/api/v2/connection?include_secrets=true&connector_names=github',
    {
      headers: {
        'Accept': 'application/json',
        'X_REPLIT_TOKEN': xReplitToken
      }
    }
  ).then(res => res.json()).then(data => data.items?.[0]);

  const accessToken = connectionSettings?.settings?.access_token || connectionSettings.settings?.oauth?.credentials?.access_token;

  if (!connectionSettings || !accessToken) {
    throw new Error('GitHub not connected');
  }
  return accessToken;
}

// WARNING: Never cache this client.
// Access tokens expire, so a new client must be created each time.
// Always call this function again to get a fresh client.
async function getUncachableGitHubClient() {
  const accessToken = await getAccessToken();
  return new Octokit({ auth: accessToken });
}

async function createGitHubRepository(octokit, repoName, description = '') {
  try {
    const response = await octokit.repos.createForAuthenticatedUser({
      name: repoName,
      description: description,
      private: false, // Make it public, change to true for private repo
      auto_init: false
    });
    return response.data;
  } catch (error) {
    if (error.status === 422) {
      console.log(`Repository ${repoName} already exists. Using existing repository.`);
      const user = await octokit.users.getAuthenticated();
      return {
        clone_url: `https://github.com/${user.data.login}/${repoName}.git`,
        html_url: `https://github.com/${user.data.login}/${repoName}`
      };
    }
    throw error;
  }
}

async function initializeGitRepo(repoUrl, accessToken) {
  const commands = [
    'git init',
    'git add .',
    'git commit -m "Initial commit: Calendar application with admin/viewer modes"',
    `git remote add origin ${repoUrl.replace('https://', `https://${accessToken}@`)}`,
    'git branch -M main',
    'git push -u origin main'
  ];

  for (const command of commands) {
    try {
      console.log(`Executing: ${command.replace(accessToken, '***')}`);
      execSync(command, { stdio: 'inherit', cwd: process.cwd() });
    } catch (error) {
      if (command.includes('git push') && error.message.includes('up-to-date')) {
        console.log('Repository is already up-to-date');
        continue;
      }
      throw error;
    }
  }
}

async function main() {
  try {
    console.log('ðŸš€ Starting GitHub backup process...');

    // Get GitHub client
    const octokit = await getUncachableGitHubClient();
    const user = await octokit.users.getAuthenticated();
    console.log(`âœ… Connected to GitHub as: ${user.data.login}`);

    // Create repository
    const repoName = 'calendar-app';
    const description = 'Local-first calendar application with natural language input, drag-and-drop, PowerPoint export, and admin/viewer modes';

    console.log(`ðŸ“ Creating repository: ${repoName}`);
    const repo = await createGitHubRepository(octokit, repoName, description);
    console.log(`âœ… Repository created/found: ${repo.html_url}`);

    // Get access token for git operations
    const accessToken = await getAccessToken();

    // Initialize git and push
    console.log('ðŸ“¤ Initializing git repository and pushing code...');
    await initializeGitRepo(repo.clone_url, accessToken);

    console.log('ðŸŽ‰ Backup completed successfully!');
    console.log(`ðŸ“ Your calendar project is now backed up at: ${repo.html_url}`);

    return {
      success: true,
      repositoryUrl: repo.html_url,
      username: user.data.login,
      repositoryName: repoName
    };

  } catch (error) {
    console.error('âŒ Backup failed:', error.message);
    return {
      success: false,
      error: error.message
    };
  }
}

// Run the backup
main().then(result => {
  console.log('\n' + '='.repeat(50));
  if (result.success) {
    console.log('âœ… BACKUP SUCCESSFUL');
    console.log(`ðŸ”— Repository: ${result.repositoryUrl}`);
  } else {
    console.log('âŒ BACKUP FAILED');
    console.log(`ðŸ’¥ Error: ${result.error}`);
    process.exit(1);
  }
}).catch(error => {
  console.error('ðŸ’¥ Fatal error:', error);
  process.exit(1);
});

