import { Octokit } from '@octokit/rest';
import fs from 'fs';
import path from 'path';

let connectionSettings;

async function getAccessToken() {
  if (connectionSettings && connectionSettings.settings.expires_at && new Date(connectionSettings.settings.expires_at).getTime() > Date.now()) {
    return connectionSettings.settings.access_token;
  }

  const hostname = process.env.REPLIT_CONNECTORS_HOSTNAME;
  const xReplitToken = process.env.REPL_IDENTITY 
    ? 'repl ' + process.env.REPL_IDENTITY 
    : process.env.WEB_REPL_RENEWAL 
    ? 'depl ' + process.env.WEB_REPL_RENEWAL 
    : null;

  if (!xReplitToken) {
    throw new Error('X_REPLIT_TOKEN not found for repl/depl');
  }

  connectionSettings = await fetch(
    'https://' + hostname + '/api/v2/connection?include_secrets=true&connector_names=github',
    {
      headers: {
        'Accept': 'application/json',
        'X_REPLIT_TOKEN': xReplitToken
      }
    }
  ).then(res => res.json()).then(data => data.items?.[0]);

  const accessToken = connectionSettings?.settings?.access_token || connectionSettings.settings?.oauth?.credentials?.access_token;

  if (!connectionSettings || !accessToken) {
    throw new Error('GitHub not connected');
  }
  return accessToken;
}

async function getUncachableGitHubClient() {
  const accessToken = await getAccessToken();
  return new Octokit({ auth: accessToken });
}

async function getAllFiles(dir, ignore = []) {
  const files = [];
  const items = fs.readdirSync(dir);

  for (const item of items) {
    const fullPath = path.join(dir, item);
    const relativePath = path.relative('.', fullPath);

    // Skip ignored files and directories
    if (ignore.some(pattern => relativePath.includes(pattern))) {
      continue;
    }

    const stat = fs.statSync(fullPath);
    if (stat.isDirectory()) {
      files.push(...await getAllFiles(fullPath, ignore));
    } else {
      files.push(relativePath);
    }
  }
  return files;
}

async function uploadFileToGitHub(octokit, owner, repo, filePath, content, message) {
  try {
    // Check if file exists
    let sha;
    try {
      const existing = await octokit.repos.getContent({
        owner,
        repo,
        path: filePath
      });
      sha = existing.data.sha;
    } catch (error) {
      // File doesn't exist, that's okay
    }

    const params = {
      owner,
      repo,
      path: filePath,
      message,
      content: Buffer.from(content).toString('base64')
    };

    if (sha) {
      params.sha = sha;
    }

    await octokit.repos.createOrUpdateFileContents(params);
    return true;
  } catch (error) {
    console.error(`Failed to upload ${filePath}:`, error.message);
    return false;
  }
}

async function main() {
  try {
    console.log('ðŸš€ Starting GitHub file upload process...');

    // Get GitHub client
    const octokit = await getUncachableGitHubClient();
    const user = await octokit.users.getAuthenticated();
    console.log(`âœ… Connected to GitHub as: ${user.data.login}`);

    const owner = user.data.login;
    const repo = 'calendar-app';

    // Get all files to upload
    const ignorePatterns = [
      'node_modules',
      '.git',
      'dist',
      '.DS_Store',
      'server/public',
      '.replit',
      'replit.nix',
      'scripts/backup-to-github.js',
      'scripts/upload-to-github.js'
    ];

    console.log('ðŸ“ Scanning project files...');
    const files = await getAllFiles('.', ignorePatterns);
    console.log(`Found ${files.length} files to upload`);

    // Upload files
    let uploaded = 0;
    let failed = 0;

    for (const filePath of files) {
      try {
        const content = fs.readFileSync(filePath, 'utf8');
        const success = await uploadFileToGitHub(
          octokit, 
          owner, 
          repo, 
          filePath, 
          content, 
          `Upload ${filePath}`
        );

        if (success) {
          uploaded++;
          console.log(`âœ… Uploaded: ${filePath}`);
        } else {
          failed++;
        }
      } catch (error) {
        console.error(`âŒ Failed to read ${filePath}:`, error.message);
        failed++;
      }
    }

    console.log('\n' + '='.repeat(50));
    console.log('ðŸŽ‰ Upload completed!');
    console.log(`âœ… Successfully uploaded: ${uploaded} files`);
    if (failed > 0) {
      console.log(`âŒ Failed uploads: ${failed} files`);
    }
    console.log(`ðŸ“ Your calendar project is now at: https://github.com/${owner}/${repo}`);

    return {
      success: true,
      repositoryUrl: `https://github.com/${owner}/${repo}`,
      uploaded,
      failed
    };

  } catch (error) {
    console.error('âŒ Upload failed:', error.message);
    return {
      success: false,
      error: error.message
    };
  }
}

// Run the upload
main().then(result => {
  if (result.success) {
    console.log('âœ… UPLOAD SUCCESSFUL');
    console.log(`ðŸ”— Repository: ${result.repositoryUrl}`);
  } else {
    console.log('âŒ UPLOAD FAILED');
    console.log(`ðŸ’¥ Error: ${result.error}`);
    process.exit(1);
  }
}).catch(error => {
  console.error('ðŸ’¥ Fatal error:', error);
  process.exit(1);
});

