import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { useCalendarStore } from '@/stores/calendarStore';
import { parseNaturalLanguage } from '@/lib/dateParser';
import { useToast } from '@/hooks/use-toast';
import { EventType } from '@shared/schema';
import { Plus, Search, Download, Upload, Menu, PlusCircle, FileText, Calendar, Grid3X3, LayoutGrid, List, AlertTriangle } from 'lucide-react';
import BulkPasteImporter from '@/components/BulkPasteImporter';
import { DataRecoveryButton } from '@/components/DataRecoveryButton';

interface SidebarProps {
  onCreateEvent: () => void;
  onExport: () => void;
  onImport: () => void;
  onExportPptxTable: () => void;
  onExportPptxMonthGrid: () => void;
  onExportPptxQuarterGrid: () => void;
  onBulkImport?: (events: any[]) => Promise<void>;
}

const eventTypeColors: Record<EventType, string> = {
  PLANNING: '#2563eb',
  MEETING: '#dc2626',
  MONTHLY_REVIEW: '#000000',
  HOLIDAYS: '#FF9000',
  QBR: '#ec4899',
};

const eventTypeLabels: Record<EventType, string> = {
  PLANNING: 'PI Planning',
  MEETING: 'Sprint Start',
  MONTHLY_REVIEW: 'FY Week',
  HOLIDAYS: 'Holidays',
  QBR: 'QBR',
};

export function Sidebar({ onCreateEvent, onExport, onImport, onExportPptxTable, onExportPptxMonthGrid, onExportPptxQuarterGrid, onBulkImport }: SidebarProps) {
  const {
    viewMode,
    setViewMode,
    searchQuery,
    setSearchQuery,
    filterType,
    setFilterType,
    createEvent,
    events,
    getEventsByType,
  } = useCalendarStore();

  const { toast } = useToast();
  const [quickAddInput, setQuickAddInput] = useState('');
  const [isQuickAdding, setIsQuickAdding] = useState(false);

  const handleQuickAdd = async () => {
    if (!quickAddInput.trim()) return;

    setIsQuickAdding(true);
    try {
      const parsed = parseNaturalLanguage(quickAddInput);

      if (!parsed) {
        toast({
          title: 'Parse Error',
          description: 'Could not parse the event text. Try a format like "Team Meeting on Nov 15 2-4pm"',
          variant: 'destructive',
        });
        return;
      }

      await createEvent({
        title: parsed.title,
        type: parsed.type || 'MEETING',
        date: parsed.date,
        startTime: parsed.startTime,
        endTime: parsed.endTime,
      });

      toast({
        title: 'Event created',
        description: `"${parsed.title}" has been added to your calendar.`,
      });

      setQuickAddInput('');
    } catch (error) {
      toast({
