            <h3 className="text-sm font-medium text-yellow-800">Data Recovery</h3>
          </div>
          <p className="text-xs text-yellow-700 mb-3">
            Lost events after database migration? Click to recover them from your browser storage.
          </p>
          <DataRecoveryButton />
        </div>

        {/* Quick Add Event */}
        <div className="mb-8">
          <label className="block text-sm font-medium text-gray-700 mb-3">
            Quick Add Event
          </label>
          <div className="relative">
            <Input
              type="text"
              placeholder="e.g., PI Planning on 15 Oct 9-11am"
              className="w-full px-4 py-3 pr-12 border border-gray-200 rounded-xl focus:ring-2 focus:ring-[#02C8FF] focus:border-[#02C8FF] transition-all duration-200 text-sm"
              value={quickAddInput}
              onChange={(e) => setQuickAddInput(e.target.value)}
              onKeyPress={handleKeyPress}
              disabled={isQuickAdding}
              data-testid="input-quick-add"
